"use strict";(self.webpackChunkdocs_next=self.webpackChunkdocs_next||[]).push([[5314],{87796:e=>{e.exports=JSON.parse('{"tag":{"label":"secrets","permalink":"/docs-next/pr-preview/pr-214/docs/tags/secrets","allTagsPath":"/docs-next/pr-preview/pr-214/docs/tags","count":1,"items":[{"id":"blueprints/by-use-case/security/cce-vault","title":"Secrets management with CCE and Hashicorp Vault","description":"Most modern IT setups are composed of several subsystems like databases, object stores, master controller, node access, and more. To access one component from another, some form of credentials are required. Configuring and storing these secrets directly in the components is considered as an anti-pattern, since a vulnerability of one component may iteratively and transitively affect the security of the whole setup.","permalink":"/docs-next/pr-preview/pr-214/docs/blueprints/by-use-case/security/cce-vault"}],"unlisted":false}}')}}]);