"use strict";(self.webpackChunkdocs_next=self.webpackChunkdocs_next||[]).push([[73549],{91324:e=>{e.exports=JSON.parse('{"tag":{"label":"saml","permalink":"/docs-next/pr-preview/pr-241/docs/tags/saml","allTagsPath":"/docs-next/pr-preview/pr-241/docs/tags","count":1,"items":[{"id":"blueprints/by-use-case/security/zitadel/zitadel-as-identity-provider-iam","title":"ZITADEL as an Identity Provider for IAM","description":"This guide outlines the steps required to integrate ZITADEL with the Identity and Access Management (IAM) of Open Telekom Cloud using the SAML 2.0 standard. The objective is to configure ZITADEL as a trusted external Identity Provider (IdP), enabling federated login for OTC users and services. This setup allows your ZITADEL-managed identities to authenticate against Open Telekom Cloud resources, forming a bridge between your custom IAM solution and the native authentication infrastructure of Open Telekom Cloud.","permalink":"/docs-next/pr-preview/pr-241/docs/blueprints/by-use-case/security/zitadel/zitadel-as-identity-provider-iam"}],"unlisted":false}}')}}]);