"use strict";(self.webpackChunkdocs_next=self.webpackChunkdocs_next||[]).push([[36160],{79306:e=>{e.exports=JSON.parse('{"tag":{"label":"storage","permalink":"/docs-next/pr-preview/pr-266/docs/tags/storage","allTagsPath":"/docs-next/pr-preview/pr-266/docs/tags","count":8,"items":[{"id":"best-practices/storage/object-storage-service/accessing-obs-through-an-nginx-reverse-proxy","title":"Accessing OBS Through an NGINX Reverse Proxy","description":"Generally, you can access OBS using a bucket\'s access domain name (for example, https://bucketname.obs.eu-de.otc.t-systems.com) provided by OBS or using a user-defined domain name bound to an OBS bucket.","permalink":"/docs-next/pr-preview/pr-266/docs/best-practices/storage/object-storage-service/accessing-obs-through-an-nginx-reverse-proxy"},{"id":"blueprints/by-use-case/storage/nextcloud/deploy","title":"Deploying Nextcloud on Open Telekom Cloud","description":"This guide walks through deploying a highly available Nextcloud setup on an Open Telekom Cloud (OTC) CCE (managed Kubernetes cluster), using OTC Object Storage Service (OBS) for file storage and OTC Relational Database Service (RDS) for Nextcloud\'s database. We will use the Nextcloud Helm charts.","permalink":"/docs-next/pr-preview/pr-266/docs/blueprints/by-use-case/storage/nextcloud/deploy"},{"id":"blueprints/by-use-case/storage/nextcloud/introduction","title":"Introduction to Nextcloud","description":"Why Should One Use Nextcloud?","permalink":"/docs-next/pr-preview/pr-266/docs/blueprints/by-use-case/storage/nextcloud/introduction"},{"id":"best-practices/storage/object-storage-service/migrating-local-data-to-obs","title":"Migrating Local Data to OBS","description":"Background","permalink":"/docs-next/pr-preview/pr-266/docs/best-practices/storage/object-storage-service/migrating-local-data-to-obs"},{"id":"best-practices/storage/elastic-volume-service/raid-array-creation-with-evs-disks","title":"RAID Array Creation with EVS Disks","description":"Redundant Array of Independent Disks (RAID) is a technology that combines multiple physical disks into one or more logical units for the purposes of data redundancy and performance improvement.","permalink":"/docs-next/pr-preview/pr-266/docs/best-practices/storage/elastic-volume-service/raid-array-creation-with-evs-disks"},{"id":"blueprints/by-use-case/storage/nextcloud/collabora","title":"Real-Time Collaboration with Collabora Online","description":"What is Collabora Online?","permalink":"/docs-next/pr-preview/pr-266/docs/blueprints/by-use-case/storage/nextcloud/collabora"},{"id":"blueprints/by-use-case/storage/nextcloud/Talk","title":"Real-Time Communication with Nextcloud Talk","description":"What is Nextcloud Talk?","permalink":"/docs-next/pr-preview/pr-266/docs/blueprints/by-use-case/storage/nextcloud/Talk"},{"id":"blueprints/by-use-case/storage/zone-redundant-nfs","title":"Zone-redundant NFS Server","description":"The main target of this blueprint is to provide a zone-redundant NFS Server with the option to perform a failover. We are going to use ECS, EVS and Storage Disaster Recovery Service of Open Telekom Cloud for that. Additionally we will cover operational tasks like extending the size of the NFS Share.","permalink":"/docs-next/pr-preview/pr-266/docs/blueprints/by-use-case/storage/zone-redundant-nfs"}],"unlisted":false}}')}}]);