"use strict";(self.webpackChunkdocs_next=self.webpackChunkdocs_next||[]).push([[22296],{70935:e=>{e.exports=JSON.parse('{"tag":{"label":"federation","permalink":"/docs-next/pr-preview/pr-272/docs/tags/federation","allTagsPath":"/docs-next/pr-preview/pr-272/docs/tags","count":2,"items":[{"id":"blueprints/by-use-case/security/authentik/identity-federation-github","title":"Identity Federation with GitHub","description":"Identity Federation with GitHub refers to the process of allowing users to authenticate using their GitHub credentials to access various applications and services. This method leverages GitHub as an identity provider (IdP), enabling single sign-on (SSO) capabilities and simplifying user management across different platforms.","permalink":"/docs-next/pr-preview/pr-272/docs/blueprints/by-use-case/security/authentik/identity-federation-github"},{"id":"blueprints/by-use-case/security/keycloak/identity-federation-github","title":"Identity Federation with GitHub","description":"Identity Federation in Keycloak refers to the ability to use external identity providers to authenticate users in your application. In this context, GitHub can be used as an identity provider, allowing users to log in to your Open Telekom Cloud tenant using their GitHub credentials. Users can choose to log in with their GitHub accounts and Keycloak takes care of the authentication process, providing a seamless experience for users while ensuring security and centralized identity management for external accounts that are not actively managed in your tenant\'s IAM.","permalink":"/docs-next/pr-preview/pr-272/docs/blueprints/by-use-case/security/keycloak/identity-federation-github"}],"unlisted":false}}')}}]);