"use strict";(self.webpackChunkdocs_next=self.webpackChunkdocs_next||[]).push([[40617],{57075:e=>{e.exports=JSON.parse('{"tag":{"label":"dns","permalink":"/docs-next/pr-preview/pr-276/docs/tags/dns","allTagsPath":"/docs-next/pr-preview/pr-276/docs/tags","count":3,"items":[{"id":"best-practices/networking/domain-name-service/configuring-private-domain-names-for-ecss","title":"Configuring Private Domain Names for ECSs","description":"If one of your ECSs is not working normally and you need to use the backup ECS to handle requests, but you have not configured private zones for the two ECSs, you need to change the private IP address in the code for the faulty ECS. This will interrupt your services and cause you to publish your website again, which is time- and labor-consuming.","permalink":"/docs-next/pr-preview/pr-276/docs/best-practices/networking/domain-name-service/configuring-private-domain-names-for-ecss"},{"id":"blueprints/by-use-case/networking/create-a-public-dns-endpoint-with-externaldns","title":"Create a Public DNS Endpoint with ExternalDNS","description":"ExternalDNS is a Kubernetes component used to manage DNS records for services and applications running in a Kubernetes cluster. It automates the creation, update, and deletion of DNS records based on the state of resources within the cluster. ExternalDNS is typically employed in scenarios where you need to expose services running inside a Kubernetes cluster to the outside world with fully qualified domain names (FQDNs), ensuring they are accessible by external users.","permalink":"/docs-next/pr-preview/pr-276/docs/blueprints/by-use-case/networking/create-a-public-dns-endpoint-with-externaldns"},{"id":"blueprints/by-use-case/security/keycloak/cce-keycloak","title":"Deploy Keycloak on CCE","description":"In this blueprint, we are going to discuss the steps to install Keycloak, in Open Telekom Cloud, on a CCE Cluster.","permalink":"/docs-next/pr-preview/pr-276/docs/blueprints/by-use-case/security/keycloak/cce-keycloak"}],"unlisted":false}}')}}]);