"use strict";(self.webpackChunkdocs_next=self.webpackChunkdocs_next||[]).push([[6630],{98762:e=>{e.exports=JSON.parse('{"tag":{"label":"saml","permalink":"/docs-next/pr-preview/pr-302/docs/tags/saml","allTagsPath":"/docs-next/pr-preview/pr-302/docs/tags","count":2,"items":[{"id":"blueprints/by-use-case/security/microsoft/active-directory-as-identity-provider-iam","title":"Active Directory as Identity Provider for IAM","description":"In this guide, we\'ll walk you through extending your on\u2011premises Microsoft Active Directory Domain Services (AD\xa0DS) and Active Directory Federation Services (AD\xa0FS) in order to establish a secure, claims\u2011based federation with Open\xa0Telekom\xa0Cloud\'s Identity and Access Management (IAM) platform. We are going to leverage the SAML\xa02.0 protocol to achieve seamless single\u2011sign\u2011on between your on-premises corporate directory and the Open Telekom Cloud IAM, ensuring both strong security and a smooth user experience.","permalink":"/docs-next/pr-preview/pr-302/docs/blueprints/by-use-case/security/microsoft/active-directory-as-identity-provider-iam"},{"id":"blueprints/by-use-case/security/zitadel/zitadel-as-identity-provider-iam","title":"ZITADEL as an Identity Provider for IAM","description":"This guide outlines the steps required to integrate ZITADEL with the Identity and Access Management (IAM) of Open Telekom Cloud using the SAML 2.0 standard. The objective is to configure ZITADEL as a trusted external Identity Provider (IdP), enabling federated login for OTC users and services. This setup allows your ZITADEL-managed identities to authenticate against Open Telekom Cloud resources, forming a bridge between your custom IAM solution and the native authentication infrastructure of Open Telekom Cloud.","permalink":"/docs-next/pr-preview/pr-302/docs/blueprints/by-use-case/security/zitadel/zitadel-as-identity-provider-iam"}],"unlisted":false}}')}}]);