"use strict";(self.webpackChunkdocs_next=self.webpackChunkdocs_next||[]).push([[36409],{44848:e=>{e.exports=JSON.parse('{"tag":{"label":"load-balancing","permalink":"/docs-next/pr-preview/pr-302/docs/tags/load-balancing","allTagsPath":"/docs-next/pr-preview/pr-302/docs/tags","count":3,"items":[{"id":"best-practices/networking/elastic-load-balancing/routing-traffic-to-backend-servers-in-different-vpcs-from-the-load-balancer","title":"Routing Traffic to Backend Servers in Different VPCs from the Load Balancer","description":"You can use ELB to route traffic to backend servers in two VPCs connected over a VPC peering connection.","permalink":"/docs-next/pr-preview/pr-302/docs/best-practices/networking/elastic-load-balancing/routing-traffic-to-backend-servers-in-different-vpcs-from-the-load-balancer"},{"id":"best-practices/networking/elastic-load-balancing/routing-traffic-to-backend-servers-in-the-same-vpc-as-the-load-balancer","title":"Routing Traffic to Backend Servers in the Same VPC as the Load Balancer","description":"You can route traffic to backend servers in the VPC where the load balancer is running.","permalink":"/docs-next/pr-preview/pr-302/docs/best-practices/networking/elastic-load-balancing/routing-traffic-to-backend-servers-in-the-same-vpc-as-the-load-balancer"},{"id":"best-practices/networking/elastic-load-balancing/using-advanced-forwarding-for-application-iteration","title":"Using Advanced Forwarding for Application Iteration","description":"As the business grows, you may need to upgrade your application. Both the old and new versions are used. Now, the new version is optimized based on users\' feedback, and you want all the users to use the new version. In this process, you can use advanced forwarding to route requests to different versions.","permalink":"/docs-next/pr-preview/pr-302/docs/best-practices/networking/elastic-load-balancing/using-advanced-forwarding-for-application-iteration"}],"unlisted":false}}')}}]);