"use strict";(self.webpackChunkdocs_next=self.webpackChunkdocs_next||[]).push([[5520],{15411:e=>{e.exports=JSON.parse('{"categoryGeneratedIndex":{"title":"Dedicated Host","description":"Dedicated Host (DeH) is a service that provides dedicated physical hosts. You can create Elastic Cloud Servers (ECSs) on a DeH to enhance the isolation, security, and performance of your ECSs. When you migrate services to a DeH, you can continue to use your server software licenses used before the migration. That is, you can use the Bring Your License (BYOL) feature on the DeH to reduce costs and independently manage your ECSs.","keywords":["dedicated-host"],"slug":"/best-practices/computing/dedicated-host","permalink":"/docs-next/pr-preview/pr-330/docs/best-practices/computing/dedicated-host","sidebar":"bestPracticesSidebar","navigation":{"previous":{"title":"Bare Metal","permalink":"/docs-next/pr-preview/pr-330/docs/best-practices/computing/bare-metal-server"},"next":{"title":"Elastic Cloud Server","permalink":"/docs-next/pr-preview/pr-330/docs/best-practices/computing/elastic-cloud-server"}}}}')}}]);