"use strict";(self.webpackChunkdocs_next=self.webpackChunkdocs_next||[]).push([[87206],{13330:e=>{e.exports=JSON.parse('{"tag":{"label":"telecommunications","permalink":"/docs-next/pr-preview/pr-339/docs/tags/telecommunications","allTagsPath":"/docs-next/pr-preview/pr-339/docs/tags","count":1,"items":[{"id":"blueprints/by-industry/telecoms/fiber-factory","title":"Fiber-Factory: Accelerated Grid Expansion","description":"More than 33 million households in Germany can access speeds of over 50 MBit/s, thanks to Telekom\'s fiber optic expansion. This enables people to work from home, streaming, and business line connectivity. Now Telekom is planning to expand the network for up to two million more households each year starting in 2021. This involved extensive construction measures. In order to plan these more efficiently, secure resources from the Open Telekom Cloud are providing support.","permalink":"/docs-next/pr-preview/pr-339/docs/blueprints/by-industry/telecoms/fiber-factory"}],"unlisted":false}}')}}]);