"use strict";(self.webpackChunkdocs_next=self.webpackChunkdocs_next||[]).push([[85501],{54576:e=>{e.exports=JSON.parse('{"tag":{"label":"sg","permalink":"/docs-next/pr-preview/pr-351/docs/tags/sg","allTagsPath":"/docs-next/pr-preview/pr-351/docs/tags","count":1,"items":[{"id":"best-practices/databases/relational-database-service/configure-sg-for-rds-instances","title":"Configure Security Groups for PostgreSQL RDS Instances and Clients","description":"Two distinct Security Groups will be required for every RDS setup. The first will be assigned to the RDS instance/nodes, and must allow inbound traffic on port 5432, which is the default PostgreSQL port. If both the RDS and client nodes are deployed within the same Subnet, this rule can be restricted to that Subnet\u2019s IP range for tighter control. This ensures that only internal workloads\u2014such can initiate connections to the database, minimizing exposure and adhering to the principle of least privilege.","permalink":"/docs-next/pr-preview/pr-351/docs/best-practices/databases/relational-database-service/configure-sg-for-rds-instances"}],"unlisted":false}}')}}]);